operation.add:
function curl_get($url) {
    ob_start();
    $ch = curl_init();
    curl_setopt_array(
        $ch,
        [
            CURLOPT_URL    => $url,
            CURLOPT_HEADER => false
        ]
    );
    curl_exec($ch);
    curl_close($ch);
    $content = ob_get_clean();

    return $content;
}
